package week2.day1;

public class If {
	// In this class we will be checking how if and if else works

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 01;
		//Give if statement with a condition and for else we don't need condition
		if (a>0) {
		 System.out.println("Positive");
		}
		else {
			System.out.println("Negative");
		}

	}

}
